    <DataSet Name="SummaryData">
      <Query>
        <DataSourceName>CustomerDB</DataSourceName>
        <CommandText>Declare @s int, @DateFrom date, @DateTo date
IF(@Store=1) set @s=1 ELSE set @s=-1
set @DateFrom = @DateF set @DateTo=@DateT

SELECT c.Code+ '' '' + c.DisplayName [Company], s.DisplayName [Store], t.TransactionNumber, tt.Name [TransactionType], SUM(ti.Amount) [AmountBeforeDid], 
CASE WHEN SUM(ti.Amount) &lt;&gt; 0 then (100-(SUM(ti.Amount)+SUM(ti.Discount))/SUM(ti.Amount)*100)/100 else 0 end [DiscountPer], SUM(ti.Amount+ti.Discount) [Amount], 
SUM(ti.Quantity) [Quantity], ((CAST(ISNULL(cr.CountReturns,0) AS numeric(15,4))/CAST(cs.CountSales AS numeric(15,4)))) [ReturnsPer]
, ((CAST(ISNULL(ccr.CountReturns,0) AS numeric(15,4))/CAST(ccs.CountSales AS numeric(15,4)))) [ReturnsPerStore], t.TransactionDate
FROM dbo.[Transaction] t
INNER JOIN dbo.TransactionItem ti ON ti.TransactionID=t.ID and t.TransactionTypeID in (@TranType) and Quantity&lt;&gt;0 and @s=1 and CAST(TransactionDate as Date) between @DateFrom and @DateTo 
INNER JOIN dbo.Location l ON t.LocationID=l.ID
INNER JOIN dbo.Store s ON s.ID=l.StoreID
INNER JOIN dbo.Company c ON l.CompanyID=c.ID
INNER JOIN dbo.TransactionType tt ON tt.ID=t.TransactionTypeID
INNER JOIN (
	SELECT Count(t.TransactionNumber) as CountSales, s.ID FROM dbo.[Transaction] t
	INNER JOIN dbo.Location l ON t.LocationID=l.ID  and t.TransactionTypeID=2 and CAST(TransactionDate as Date) between @DateFrom and @DateTo
	INNER JOIN dbo.Store s ON s.ID=l.StoreID
	WHERE s.ID IN (@DataStore)
	GROUP BY s.ID
	) as cs ON s.ID=cs.ID
INNER JOIN (
	SELECT Count(t.TransactionNumber) as CountSales, c.ID FROM dbo.[Transaction] t
	INNER JOIN dbo.Location l ON t.LocationID=l.ID  and t.TransactionTypeID=2 and CAST(TransactionDate as Date) between @DateFrom and @DateTo
	INNER JOIN dbo.Company c ON l.CompanyID=c.ID
	WHERE c.ID IN (@DataCompany)
	GROUP BY c.ID
	) as ccs ON c.ID=ccs.ID
LEFT JOIN (
	SELECT Count(t.TransactionNumber) as CountReturns, s.ID FROM dbo.[Transaction] t
	INNER JOIN dbo.Location l ON t.LocationID=l.ID  and t.TransactionTypeID=6 and CAST(TransactionDate as Date) between @DateFrom and @DateTo
	INNER JOIN dbo.Store s ON s.ID=l.StoreID
	WHERE s.ID IN (@DataStore)
	GROUP BY s.ID
	) as cr ON s.ID=cr.ID
LEFT JOIN (
	SELECT Count(t.TransactionNumber) as CountReturns, c.ID FROM dbo.[Transaction] t
	INNER JOIN dbo.Location l ON t.LocationID=l.ID  and t.TransactionTypeID=6 and CAST(TransactionDate as Date) between @DateFrom and @DateTo
	INNER JOIN dbo.Company c ON l.CompanyID=c.ID
	WHERE c.ID IN (@DataCompany)
	GROUP BY c.ID
	) as ccr ON c.ID=ccr.ID
WHERE Quantity&lt;&gt;0
and s.ID IN (@DataStore) 
GROUP BY c.Code+ '' '' + c.DisplayName, s.DisplayName, t.TransactionNumber,cr.CountReturns,cs.CountSales,ccr.CountReturns,ccs.CountSales, tt.Name, t.TransactionDate
having SUM(ti.Quantity) &lt;&gt; 0 and SUM(ti.Amount+ti.Discount) &gt;= ISNULL(cast(@MinAmount as numeric(25,6)),SUM(ti.Amount+ti.Discount)) and SUM(ti.Amount+ti.Discount) &lt;= ISNULL(cast(@MaxAmount as numeric(25,6)),SUM(ti.Amount+ti.Discount))
ORDER BY 1,2</CommandText>
