        <CommandText>Declare @s int, @DateFrom date, @DateTo date
IF(@Store=2) set @s=1 ELSE set @s=-1
IF(@DateF is not NULL and @DateT is not NULL) begin set @DateFrom = @DateF set @DateTo=@DateT end 
ELSE IF(@TimeSlots=1)
begin set @DateFrom = Getdate() set @DateTo = Getdate() end
ELSE IF(@TimeSlots=2)
begin set @DateFrom = Getdate()-1 set @DateTo = Getdate()-1 end
ELSE IF(@TimeSlots=3)
begin set @DateFrom = dateadd(day, 1-datepart(dw, getdate()), CONVERT(date,getdate())) set @DateTo = dateadd(day, 7-datepart(dw, getdate()), CONVERT(date,getdate())) end
ELSE IF(@TimeSlots=4)
begin set @DateFrom = dateadd(dd, -day(GETDATE()), GETDATE())+1 set @DateTo = dateadd(dd, -day(dateadd(mm, 1, GETDATE())), dateadd(mm, 1, GETDATE())) end
ELSE IF(@TimeSlots=5)
begin set @DateFrom = CAST(CAST(YEAR(Getdate()) AS nvarchar)+''-01-01'' AS DATE) set @DateTo = CAST(CAST(YEAR(Getdate()) AS nvarchar)+''-12-31'' AS DATE)end

select c.Code+ '' '' + c.DisplayName [Company]
, s.DisplayName [Store]
, e.FirstName + '' '' + e.LastName [Employee]
--, dateadd(SECOND, DATEDIFF(second, ClockInDateTime, ClockOutDateTime), Cast(''00:00:00.0000'' as time)) [Worked hours]
, CASE WHEN FLOOR(CAST((DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)&lt;10 
	THEN ''0''+CAST(FLOOR(CAST((DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100))
	ELSE CAST(FLOOR(CAST((DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100)) END + '':'' 
	+ CASE WHEN FLOOR(((DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST((DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60)&lt;10
	  THEN ''0''+CAST(FLOOR(((DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST((DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60) AS NVARCHAR(3))
	  ELSE CAST(FLOOR(((DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST((DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60) AS NVARCHAR(3)) END [Worked hours]
, cast(ClockInDateTime as time) [Sing in]
, cast(ClockOutDateTime as time) [Sing out]
, CAST(ClockInDateTime AS DATE) [Date] 
--, CAST(ClockOutDateTime AS DATE) [Date2] 
, CASE 
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 1 THEN ''Sunday''
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 2 THEN ''Monday''
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 3 THEN ''Tuesday''
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 4 THEN ''Wednesday''
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 5 THEN ''Thursday''
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 6 THEN ''Friday''
	WHEN DATEPART(WEEKDAY,ClockInDateTime) = 7 THEN ''Saturday''
END[Day] 
, esr.Name [Skip]
, TotalEmployeeData.TotalEmployeeData
, TotalEmployee.TotalEmployee
, TotalStore.TotalStore
, TotalCompany.TotalCompany
, TotalAll.TotalAll
,DATEPART(WEEKDAY,ClockInDateTime)[DayNumber]
,(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) [Worked hours sec]
FROM dbo.EmployeeClockInOutEvent ecioe
INNER JOIN dbo.Employee e ON e.ID=ecioe.EmployeeID and @s=1 and (ClockInDateTime is not null and ClockOutDateTime is not null) and CAST(ClockInDateTime as Date) between @DateFrom and @DateTo 
INNER JOIN dbo.Store s ON s.ID=ecioe.StoreID
INNER JOIN dbo.Company c ON c.ID=s.CompanyID
INNER JOIN
(
	SELECT c.id [CompanyId], s.id [StoreId], e.ID [EmployeeId], CAST(ClockInDateTime AS DATE) [Date]
	, CASE WHEN FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)&lt;10 
	THEN ''0''+CAST(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100))
	ELSE CAST(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100)) END + '':'' 
	+ CASE WHEN FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60)&lt;10
	  THEN ''0''+CAST(FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60) AS NVARCHAR(3))
	  ELSE CAST(FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60) AS NVARCHAR(3)) END [TotalEmployeeData]
	FROM dbo.EmployeeClockInOutEvent ecioe
	INNER JOIN dbo.Employee e ON e.ID=ecioe.EmployeeID and @s=1 and (ClockInDateTime is not null and ClockOutDateTime is not null) and CAST(ClockInDateTime as Date) between @DateFrom and @DateTo 
	INNER JOIN dbo.Store s ON s.ID=ecioe.StoreID
	INNER JOIN dbo.Company c ON c.ID=s.CompanyID
	WHERE s.ID IN (@DataStore)
	GROUP BY  c.id, s.id, e.ID, CAST(ClockInDateTime AS DATE) 
) AS TotalEmployeeData ON TotalEmployeeData.CompanyId=c.id and TotalEmployeeData.StoreId=s.id and TotalEmployeeData.EmployeeId=e.ID and CAST(ClockInDateTime AS DATE)=TotalEmployeeData.Date
INNER JOIN
(
	SELECT c.id [CompanyId], s.id [StoreId], e.ID [EmployeeId]
	, CASE WHEN FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)&lt;10 
	THEN ''0''+CAST(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100))
	ELSE CAST(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100)) END + '':'' 
	+ CASE WHEN FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60)&lt;10
	  THEN ''0''+CAST(FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60) AS NVARCHAR(3))
	  ELSE CAST(FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60) AS NVARCHAR(3)) END [TotalEmployee]
	FROM dbo.EmployeeClockInOutEvent ecioe
	INNER JOIN dbo.Employee e ON e.ID=ecioe.EmployeeID and @s=1 and (ClockInDateTime is not null and ClockOutDateTime is not null) and CAST(ClockInDateTime as Date) between @DateFrom and @DateTo 
	INNER JOIN dbo.Store s ON s.ID=ecioe.StoreID
	INNER JOIN dbo.Company c ON c.ID=s.CompanyID
	WHERE s.ID IN (@DataStore)
	GROUP BY  c.id, s.id, e.ID 
) AS TotalEmployee ON TotalEmployee.CompanyId=c.id and TotalEmployee.StoreId=s.id and TotalEmployee.EmployeeId=e.ID
INNER JOIN
(
	SELECT c.id [CompanyId], s.id [StoreId]
	, CASE WHEN FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)&lt;10 
	THEN ''0''+CAST(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100))
	ELSE CAST(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600) AS NVARCHAR(100)) END + '':'' 
	+ CASE WHEN FLOOR((SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime))-(FLOOR(CAST(SUM(DATEDIFF(second, ClockInDateTime, ClockOutDateTime)) AS NUMERIC(16,2))/3600)*3600))/60)&lt;10
